--
-- Cấu trúc bảng cho bảng `categories`
--

CREATE TABLE `categories` (
  `id` int(11) NOT NULL,
  `name` varchar(191) NOT NULL,
  `slug` varchar(191) NOT NULL,
  `description` mediumtext DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 0,
  `image` varchar(191) NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Đang đổ dữ liệu cho bảng `categories`
--

INSERT INTO `categories` (`id`, `name`, `slug`, `description`, `status`, `image`, `created_at`) VALUES
(18, 'Áo thun', 'tai-nghe-bluetooth--39', 'Tai nghe bluetooth là dòng sản phẩm tai nghe không dây mang cho mình công nghệ bluetooth có khả năng kết nối được với máy tính hoặc thiết bị di động được sử dụng rộng rãi trên thị trường hiện nay', 0, '1655223571.png', '2022-06-14 16:19:31'),
(19, 'Quần', 'tai-nghe-chup-tai', 'Tai nghe chụp tai hay tai nghe trùm đầu là một loại tai nghe có cấu tạo khá phức tạp và kích thước lớn, dùng để bao toàn bộ tai của người dùng. Loại tai nghe này ra đời nhằm mục đích mang lại cho người dùng cảm giác thoải mái, dễ chịu nhất khi sử dụng. Ưu điểm: Thiết kế đẹp, nhiều kiểu dáng để bạn lựa chọn', 0, '1655223651.png', '2022-06-14 16:20:51'),
(20, 'Phụ kiện', 'tai-nghe-co-day-11', 'Tai nghe có dây vẫn còn được được tin dùng vì chất lượng âm thanh mà nó mang lại và hạn chế bị mất chất lượng', 0, '1655223689.png', '2022-06-14 16:21:29');

